package com.nissan.training_core_java;

public class operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		int c=30;
		System.out.println(a<b&&a<c);
		System.out.println(a<b&a<c);
		System.out.println(a<b||a<c);
		System.out.println(a<b|a<c);

	}

}
